mapper.insert:

INSERT INTO TB_BLEREQUEST (
	BLE_REQUEST_IDX
	, SCHEDULE_IDX
	, USER_ID
	, UUID
	, REQUEST_DATETIME
	, RECOGNITION_DATETIME
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{bleRequestIdx}
	, #{scheduleIdx}
	, #{userId}
	, #{uuid}
	, #{requestDatetime}
	, #{recognitionDatetime}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_BLEREQUEST SET BLE_REQUEST_IDX = #{bleRequestIdx}


	, SCHEDULE_IDX = #{scheduleIdx}


	, USER_ID = #{userId}


	, UUID = #{uuid}


	, REQUEST_DATETIME = #{requestDatetime}


	, RECOGNITION_DATETIME = #{recognitionDatetime}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE BLE_REQUEST_IDX = #{bleRequestIdx}

SELECT T_BLEREQUEST.BLE_REQUEST_IDX , T_BLEREQUEST.SCHEDULE_IDX , T_BLEREQUEST.USER_ID , T_BLEREQUEST.UUID , T_BLEREQUEST.REQUEST_DATETIME , T_BLEREQUEST.RECOGNITION_DATETIME , T_BLEREQUEST.USE_YN , T_BLEREQUEST.DELETE_YN , T_BLEREQUEST.CREATE_USER_IDX , T_BLEREQUEST.UPDATE_USER_IDX , T_BLEREQUEST.DELETE_USER_IDX , T_BLEREQUEST.UPDATE_DATETIME , T_BLEREQUEST.CREATE_DATETIME , T_BLEREQUEST.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME , T_YEAR_SEMESTER.YEAR , T_YEAR_SEMESTER.SEMESTER , T_COLLEGE.COLLEGE_NAME , T_DEPARTMENT.DEPARTMENT_NAME , T_SUBJECT.SUBJECT_NAME , T_PROFESSOR.USER_NAME AS PROFESSOR_NAME FROM TB_BLEREQUEST AS T_BLEREQUEST LEFT JOIN TB_SCHEDULE AS T_SCHEDULE ON T_SCHEDULE.SCHEDULE_IDX = T_BLEREQUEST.SCHEDULE_IDX LEFT JOIN TB_CLASSROOM AS T_CLASSROOM ON T_CLASSROOM.CLASSROOM_IDX = T_SCHEDULE.CLASSROOM_IDX LEFT JOIN TB_YEAR_SEMESTER AS T_YEAR_SEMESTER ON T_YEAR_SEMESTER.YEAR_SEMESTER_IDX = T_CLASSROOM.YEAR_SEMESTER_IDX LEFT JOIN TB_COLLEGE AS T_COLLEGE ON T_COLLEGE.COLLEGE_IDX = T_CLASSROOM.COLLEGE_IDX LEFT JOIN TB_DEPARTMENT AS T_DEPARTMENT ON T_DEPARTMENT.DEPARTMENT_IDX = T_CLASSROOM.DEPARTMENT_IDX LEFT JOIN TB_SUBJECT AS T_SUBJECT ON T_SUBJECT.SUBJECT_IDX = T_CLASSROOM.SUBJECT_IDX LEFT JOIN TB_USER AS T_PROFESSOR ON T_PROFESSOR.USER_IDX = T_CLASSROOM.PROFESSOR_IDX LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_BLEREQUEST.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_BLEREQUEST.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_BLEREQUEST.DELETE_USER_IDX WHERE 1=1
AND T_BLEREQUEST.USE_YN = #{useYN}
AND T_BLEREQUEST.DELETE_YN = #{deleteYN}
ORDER BY T_BLEREQUEST.BLE_REQUEST_IDX DESC
