mapper.insert:

INSERT INTO TB_COLLEGE (
	COLLEGE_IDX
	, COLLEGE_NAME
	, COLLEGE_PHONE
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{collegeIdx}
	, #{collegeName}
	, #{collegePhone}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_COLLEGE SET COLLEGE_IDX = #{collegeIdx}


	, COLLEGE_NAME = #{collegeName}


	, COLLEGE_PHONE = #{collegePhone}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE COLLEGE_IDX = #{collegeIdx}

SELECT T_COLLEGE.COLLEGE_IDX , T_COLLEGE.COLLEGE_NAME , T_COLLEGE.COLLEGE_PHONE , T_COLLEGE.USE_YN , T_COLLEGE.DELETE_YN , T_COLLEGE.CREATE_USER_IDX , T_COLLEGE.UPDATE_USER_IDX , T_COLLEGE.DELETE_USER_IDX , T_COLLEGE.UPDATE_DATETIME , T_COLLEGE.CREATE_DATETIME , T_COLLEGE.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME FROM TB_COLLEGE AS T_COLLEGE LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_COLLEGE.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_COLLEGE.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_COLLEGE.DELETE_USER_IDX WHERE 1=1
AND T_COLLEGE.USE_YN = #{useYN}
AND T_COLLEGE.DELETE_YN = #{deleteYN}
ORDER BY T_COLLEGE.COLLEGE_NAME DESC
