mapper.insert:

INSERT INTO TB_SCHEDULE (
	SCHEDULE_IDX
	, CLASSROOM_IDX
	, SCHEDULETIME_IDX
	, WEEK
	, SCHEDULE_DATE
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{scheduleIdx}
	, #{classroomIdx}
	, #{scheduletimeIdx}
	, #{week}
	, #{scheduleDate}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_SCHEDULE SET SCHEDULE_IDX = #{scheduleIdx}


	, CLASSROOM_IDX = #{classroomIdx}


	, SCHEDULETIME_IDX = #{scheduletimeIdx}


	, WEEK = #{week}


	, SCHEDULE_DATE = #{scheduleDate}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE SCHEDULE_IDX = #{scheduleIdx}

SELECT T_SCHEDULE.SCHEDULE_IDX , T_SCHEDULE.CLASSROOM_IDX , T_SCHEDULE.SCHEDULETIME_IDX , T_SCHEDULE.WEEK , T_SCHEDULE.SCHEDULE_DATE , T_SCHEDULE.USE_YN , T_SCHEDULE.DELETE_YN , T_SCHEDULE.CREATE_USER_IDX , T_SCHEDULE.UPDATE_USER_IDX , T_SCHEDULE.DELETE_USER_IDX , T_SCHEDULE.UPDATE_DATETIME , T_SCHEDULE.CREATE_DATETIME , T_SCHEDULE.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME , T_DEPARTMENT.DEPARTMENT_IDX , T_DEPARTMENT.DEPARTMENT_NAME , T_YEAR_SEMESTER.YEAR_SEMESTER_IDX , T_YEAR_SEMESTER.YEAR , T_YEAR_SEMESTER.SEMESTER , T_SUBJECT.SUBJECT_IDX , T_SUBJECT.SUBJECT_NAME , T_SCHEDULETIME.CLASS_TIME , T_SCHEDULETIME.START_TIME , T_SCHEDULETIME.END_TIME , T_CLASSROOM.CLASSROOM FROM TB_SCHEDULE AS T_SCHEDULE LEFT JOIN TB_SCHEDULETIME AS T_SCHEDULETIME ON T_SCHEDULETIME.SCHEDULETIME_IDX = T_SCHEDULE.SCHEDULETIME_IDX LEFT JOIN TB_CLASSROOM AS T_CLASSROOM ON T_CLASSROOM.CLASSROOM_IDX = T_SCHEDULE.CLASSROOM_IDX LEFT JOIN TB_DEPARTMENT AS T_DEPARTMENT ON T_DEPARTMENT.DEPARTMENT_IDX = T_CLASSROOM.DEPARTMENT_IDX LEFT JOIN TB_SUBJECT AS T_SUBJECT ON T_SUBJECT.SUBJECT_IDX = T_CLASSROOM.SUBJECT_IDX LEFT JOIN TB_YEAR_SEMESTER AS T_YEAR_SEMESTER ON T_YEAR_SEMESTER.YEAR_SEMESTER_IDX = T_CLASSROOM.YEAR_SEMESTER_IDX LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_SCHEDULE.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_SCHEDULE.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_SCHEDULE.DELETE_USER_IDX WHERE 1=1
AND T_SCHEDULE.USE_YN = #{useYN}
AND T_SCHEDULE.DELETE_YN = #{deleteYN}
ORDER BY T_SCHEDULE.SCHEDULE_IDX DESC
