mapper.insert:

INSERT INTO TB_SCHEDULETIME (
	SCHEDULETIME_IDX
	, CLASS_TIME
	, START_TIME
	, END_TIME
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{scheduletimeIdx}
	, #{classTime}
	, #{startTime}
	, #{endTime}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_SCHEDULETIME SET SCHEDULETIME_IDX = #{scheduletimeIdx}


	, CLASS_TIME = #{classTime}


	, START_TIME = #{startTime}


	, END_TIME = #{endTime}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE SCHEDULETIME_IDX = #{scheduletimeIdx}

SELECT T_SCHEDULETIME.SCHEDULETIME_IDX , T_SCHEDULETIME.CLASS_TIME , T_SCHEDULETIME.START_TIME , T_SCHEDULETIME.END_TIME , T_SCHEDULETIME.USE_YN , T_SCHEDULETIME.DELETE_YN , T_SCHEDULETIME.CREATE_USER_IDX , T_SCHEDULETIME.UPDATE_USER_IDX , T_SCHEDULETIME.DELETE_USER_IDX , T_SCHEDULETIME.UPDATE_DATETIME , T_SCHEDULETIME.CREATE_DATETIME , T_SCHEDULETIME.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME FROM TB_SCHEDULETIME AS T_SCHEDULETIME LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_SCHEDULETIME.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_SCHEDULETIME.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_SCHEDULETIME.DELETE_USER_IDX WHERE 1=1
AND T_SCHEDULETIME.USE_YN = #{useYN}
AND T_SCHEDULETIME.DELETE_YN = #{deleteYN}
ORDER BY T_SCHEDULETIME.SCHEDULETIME_IDX DESC
