mapper.insert:

INSERT INTO TB_SUBJECT (
	SUBJECT_IDX
	, COLLEGE_IDX
	, DEPARTMENT_IDX
	, SUBJECT_NAME
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{subjectIdx}
	, #{collegeIdx}
	, #{departmentIdx}
	, #{subjectName}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_SUBJECT SET SUBJECT_IDX = #{subjectIdx}


	, COLLEGE_IDX = #{collegeIdx}


	, DEPARTMENT_IDX = #{departmentIdx}


	, SUBJECT_NAME = #{subjectName}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE SUBJECT_IDX = #{subjectIdx}

SELECT T_SUBJECT.SUBJECT_IDX , T_SUBJECT.COLLEGE_IDX , T_SUBJECT.DEPARTMENT_IDX , T_SUBJECT.SUBJECT_NAME , T_SUBJECT.USE_YN , T_SUBJECT.DELETE_YN , T_SUBJECT.CREATE_USER_IDX , T_SUBJECT.UPDATE_USER_IDX , T_SUBJECT.DELETE_USER_IDX , T_SUBJECT.UPDATE_DATETIME , T_SUBJECT.CREATE_DATETIME , T_SUBJECT.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME , T_COLLEGE.COLLEGE_NAME , T_DEPARTMENT.DEPARTMENT_NAME FROM TB_SUBJECT AS T_SUBJECT LEFT JOIN TB_COLLEGE AS T_COLLEGE ON T_COLLEGE.COLLEGE_IDX = T_SUBJECT.COLLEGE_IDX LEFT JOIN TB_DEPARTMENT AS T_DEPARTMENT ON T_DEPARTMENT.DEPARTMENT_IDX = T_SUBJECT.DEPARTMENT_IDX LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_SUBJECT.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_SUBJECT.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_SUBJECT.DELETE_USER_IDX WHERE 1=1
AND T_SUBJECT.USE_YN = #{useYN}
AND T_SUBJECT.DELETE_YN = #{deleteYN}
ORDER BY T_SUBJECT.SUBJECT_NAME DESC
