mapper.insert:

INSERT INTO TB_USER (
	USER_IDX
	, COLLEGE_IDX
	, DEPARTMENT_IDX
	, USER_ID
	, PASSWORD
	, USER_NAME
	, USER_PHONE
	, EMAIL
	, GRADE
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{userIdx}
	, #{collegeIdx}
	, #{departmentIdx}
	, #{userId}
	, #{password}
	, #{userName}
	, #{userPhone}
	, #{email}
	, #{grade}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:
SELECT T_USER.USER_IDX , T_USER.USER_ID , T_USER.USER_NAME , T_USER.PASSWORD , T_USER.USER_PHONE , T_USER.EMAIL , T_USER.GRADE , T_USER.FCM_TOKEN , T_USER.COLLEGE_IDX , T_USER.DEPARTMENT_IDX , T_USER.LOGIN_IP , T_USER.LOGIN_DATETIME , T_USER.USE_YN , T_USER.DELETE_YN , T_USER.CREATE_USER_IDX , T_USER.UPDATE_USER_IDX , T_USER.DELETE_USER_IDX , T_USER.UPDATE_DATETIME , T_USER.CREATE_DATETIME , T_USER.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME , T_COLLEGE.COLLEGE_NAME , T_DEPARTMENT.DEPARTMENT_NAME FROM TB_USER AS T_USER LEFT JOIN TB_COLLEGE AS T_COLLEGE ON T_COLLEGE.COLLEGE_IDX = T_USER.COLLEGE_IDX LEFT JOIN TB_DEPARTMENT AS T_DEPARTMENT ON T_DEPARTMENT.DEPARTMENT_IDX = T_USER.DEPARTMENT_IDX LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_USER.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_USER.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_USER.DELETE_USER_IDX WHERE 1=1
AND T_USER.USER_IDX = #{userIdx}
AND T_USER.USER_ID = #{userId}
LIMIT 1


UPDATE TB_USER SET USER_IDX = #{userIdx}


	, COLLEGE_IDX = #{collegeIdx}


	, DEPARTMENT_IDX = #{departmentIdx}


	, USER_ID = #{userId}


	, PASSWORD = #{password}


	, USER_NAME = #{userName}


	, USER_PHONE = #{userPhone}


	, EMAIL = #{email}


	, GRADE = #{grade}


	, FCM_TOKEN = #{fcmToken}


	, LOGIN_IP = #{loginIp}, LOGIN_DATETIME = #{loginDateTime}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE USER_IDX = #{userIdx}

SELECT T_USER.USER_IDX , T_USER.USER_ID , T_USER.USER_NAME , T_USER.PASSWORD , T_USER.USER_PHONE , T_USER.EMAIL , T_USER.GRADE , T_USER.FCM_TOKEN , T_USER.COLLEGE_IDX , T_USER.DEPARTMENT_IDX , T_USER.LOGIN_IP , T_USER.LOGIN_DATETIME , T_USER.USE_YN , T_USER.DELETE_YN , T_USER.CREATE_USER_IDX , T_USER.UPDATE_USER_IDX , T_USER.DELETE_USER_IDX , T_USER.UPDATE_DATETIME , T_USER.CREATE_DATETIME , T_USER.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME , T_COLLEGE.COLLEGE_NAME , T_DEPARTMENT.DEPARTMENT_NAME FROM TB_USER AS T_USER LEFT JOIN TB_COLLEGE AS T_COLLEGE ON T_COLLEGE.COLLEGE_IDX = T_USER.COLLEGE_IDX LEFT JOIN TB_DEPARTMENT AS T_DEPARTMENT ON T_DEPARTMENT.DEPARTMENT_IDX = T_USER.DEPARTMENT_IDX LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_USER.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_USER.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_USER.DELETE_USER_IDX WHERE 1=1
AND T_USER.USE_YN = #{useYN}
AND T_USER.DELETE_YN = #{deleteYN}
AND T_USER.COLLEGE_IDX = #{collegeIdx}
AND T_USER.DEPARTMENT_IDX = #{departmentIdx}
AND T_USER.GRADE = #{grade}
ORDER BY T_USER.USER_NAME DESC
