mapper.insert:

INSERT INTO TB_YEAR_SEMESTER (
	YEAR_SEMESTER_IDX
	, YEAR
	, SEMESTER
	, START_DATETIME
	, END_DATETIME
	, USE_YN, DELETE_YN, CREATE_USER_IDX, CREATE_DATETIME
) VALUES (
	#{yearSemesterIdx}
	, #{year}
	, #{semester}
	, #{startDateTime}
	, #{endDateTime}
	, #{useYN}, #{deleteYN}, #{createUserIdx}, #{createDateTime}
)

mapper.select:

UPDATE TB_YEAR_SEMESTER SET YEAR_SEMESTER_IDX = #{yearSemesterIdx}


	, YEAR = #{year}


	, SEMESTER = #{semester}


	, START_DATETIME = #{startDateTime}


	, END_DATETIME = #{endDateTime}


	, USE_YN = #{useYN}


	, UPDATE_USER_IDX = #{updateUserIdx}, UPDATE_DATETIME = #{updateDateTime}



		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = #{deleteUserIdx}, DELETE_DATETIME = #{deleteDateTime}


		, DELETE_YN = #{deleteYN}, DELETE_USER_IDX = "", DELETE_DATETIME = ""


WHERE YEAR_SEMESTER_IDX = #{yearSemesterIdx}

SELECT T_YEAR_SEMESTER.YEAR_SEMESTER_IDX , T_YEAR_SEMESTER.YEAR , T_YEAR_SEMESTER.SEMESTER , T_YEAR_SEMESTER.START_DATETIME , T_YEAR_SEMESTER.END_DATETIME , T_YEAR_SEMESTER.USE_YN , T_YEAR_SEMESTER.DELETE_YN , T_YEAR_SEMESTER.CREATE_USER_IDX , T_YEAR_SEMESTER.UPDATE_USER_IDX , T_YEAR_SEMESTER.DELETE_USER_IDX , T_YEAR_SEMESTER.UPDATE_DATETIME , T_YEAR_SEMESTER.CREATE_DATETIME , T_YEAR_SEMESTER.DELETE_DATETIME , T_USER_CREATE.USER_NAME AS CREATE_USER_NAME , T_USER_UPDATE.USER_NAME AS UPDATE_USER_NAME , T_USER_DELETE.USER_NAME AS DELETE_USER_NAME FROM TB_YEAR_SEMESTER AS T_YEAR_SEMESTER LEFT JOIN TB_USER AS T_USER_CREATE ON T_USER_CREATE.USER_IDX = T_YEAR_SEMESTER.CREATE_USER_IDX LEFT JOIN TB_USER AS T_USER_UPDATE ON T_USER_UPDATE.USER_IDX = T_YEAR_SEMESTER.UPDATE_USER_IDX LEFT JOIN TB_USER AS T_USER_DELETE ON T_USER_DELETE.USER_IDX = T_YEAR_SEMESTER.DELETE_USER_IDX WHERE 1=1
AND T_YEAR_SEMESTER.USE_YN = #{useYN}
AND T_YEAR_SEMESTER.DELETE_YN = #{deleteYN}
ORDER BY T_YEAR_SEMESTER.START_DATETIME DESC
